import React, {Component} from 'react';
import './index.css';
import './Grid.css';
import Node from './Node';
const NUMBER_OF_RAW =35;
const NUMBER_OF_COLUMN =15;
const grid=[];
const START_NODE_ROW=0;
const START_NODE_COL=7;
const FINISH_NODE_ROW=NUMBER_OF_RAW-1;
const FINISH_NODE_COL=NUMBER_OF_COLUMN-1-7;
const getInitialGrid = () => {
    const grid = [];
    for (let row = 0; row < 35; row++) {
        const currentRow = [];
        for (let col = 0; col < 15; col++) {
            currentRow.push(createNode(col, row));
        }
        grid.push(currentRow);
    }
    return grid;
}ã€€
const createNode = (col, row) => {
    return {
        col,
        row,
        isStart: row === START_NODE_ROW && col === START_NODE_COL,
        isFinish: row === FINISH_NODE_ROW && col === FINISH_NODE_COL,
        distance: Infinity,
        isVisited: false,
        isWall: false,
        previousNode: null,
    };
};
const grids=getInitialGrid();
class Grid extends Component{
    constructor(props) {
        super(props);
        this.setStart = this.setStart.bind(this);
        this.setGoal  = this.setGoal.bind(this);
        this.selectOption=this.selectOption.bind(this);
        this.state={select:"-1",
        toggleButton: false};
        this.current_select={"0":this.setStart, "1":this.setGoal};
        this.putWeight=this.putWeight.bind(this)

    }
    selectOption(e){
        if(e.currentTarget.innerText==="Start"&&!this.state.toggleButton){
            this.setState(()=>({
                select: "0", toggleButton: true
            }));
            e.currentTarget.setAttribute("id", "button_on")
        }
        else if(e.currentTarget.innerText==="Start"&&this.state.toggleButton){
            this.setState(()=>({
                select: "-1", toggleButton: false
            }));
            e.currentTarget.removeAttribute("id")
        }
        else if(e.currentTarget.innerText==="End"&&!this.state.toggleButton){
            this.setState(()=>({
                select: "1",toggleButton: true
            }));
            e.currentTarget.setAttribute("id", "button_on")
        }
        else if(e.currentTarget.innerText==="End"&&this.state.toggleButton){
            this.setState(()=>({
                select: "-1",toggleButton: false
            }));
            e.currentTarget.removeAttribute("id")

        }
        console.log(this.state)
    }
    putWeight(e){
        e.currentTarget.setAttribute("id", "toggled")
    }
    setStart(e){
        document.getElementById("start").removeAttribute("disabled")
        document.getElementById("start").onmousedown=this.putWeight
        document.getElementById("start").removeAttribute("id")
        e.currentTarget.setAttribute("id", "start")
        e.currentTarget.setAttribute("disabled",true)

    };
    setGoal(e){
        document.getElementById("end").removeAttribute("disabled")
        document.getElementById("end").onmousedown=this.putWeight
        document.getElementById("end").removeAttribute("id")
        e.currentTarget.setAttribute("id", "end")
        e.currentTarget.setAttribute("disabled",true)
    };

    render() {
        return(
            <div>
            <span id="bar">
            <div className="button" onClick={this.selectOption}> Start </div>
            <div className="button" onClick={this.selectOption}> End </div>
                <div className="button" onClick={this.selectOption}> Search </div>
            </span>
        <div className='grid'>
                {grids.map((raw, rawId)=>{
                    return(
                        <div key={rawId}>
                            {raw.map((node,nodeId)=>{
                                if(rawId===0 && nodeId===0) {
                                    return (
                                        <Node key={grids[rawId][nodeId].col} nodeId={grids[rawId][nodeId].col} rawId={grids[rawId][nodeId].row} onClick={this.current_select} select={this.state.select} start={grids[rawId][nodeId].isStart} end={grids[rawId][nodeId].isFinish}></Node>
                                    )
                                }else if(rawId===NUMBER_OF_RAW-1 && nodeId===NUMBER_OF_COLUMN-1){
                                    return(
                                        <Node key={grids[rawId][nodeId].col} nodeId={grids[rawId][nodeId].col} rawId={grids[rawId][nodeId].row} onClick={this.current_select} select={this.state.select} start={grids[rawId][nodeId].isStart} end={grids[rawId][nodeId].isFinish}></Node>

                                    )
                                }
                                else{
                                    return(
                                        <Node key={grids[rawId][nodeId].col} nodeId={grids[rawId][nodeId].col} rawId={grids[rawId][nodeId].row} onClick={this.current_select} select={this.state.select} start={grids[rawId][nodeId].isStart} end={grids[rawId][nodeId].isFinish}></Node>

                                    )         }
                            })}
                        </div>
                    )
                })}
            </div>
            </div>
        )
    }
}

export default Grid;
