import React, {Component} from 'react';
import './Grid.css';
class Node extends Component {
    constructor(props) {
        super(props);
        this.nodeId= this.props.nodeId;
        this.rawId= this.props.rawId;
        this.start = this.props.start;
        this.end = this.props.end;
        this.isVisited=this.props.isVisited;
        this.color=this.props.select;
        this.searchNeighbors= this.props.searchNeighbors;
        this.putWeight=this.putWeight.bind(this);
    }
    putWeight(e){
        e.currentTarget.setAttribute("id", "toggled")
    }

    render(){
            if(this.start){
                return(<div className="node" id='start' nodeid={this.nodeId} rawid={this.rawId} onClick={this.props.onClick[this.props.select]} disabled={true}>
                </div>)
            }
            else if(this.end){
                return(<div className="node" id='end' nodeid={this.nodeId} rawid={this.rawId} onClick={this.props.onClick[this.props.select]} disabled={true}>
                </div>)
            }
            else if(this.isVisited){
                return(<div style={{backgroundColor:this.color}} className="node" nodeid={this.nodeId} rawid={this.rawId} onClick={this.props.onClick[this.props.select]}>
                </div>)
            }
            else{
            return(<div className="node" nodeid={this.nodeId} rawid={this.rawId} onClick={this.props.onClick[this.props.select]} onMouseDown={this.putWeight}>
            </div>)};

    }
}

export default Node;
